SELECT id, taxtype taxType, reimbursesubject reimburseSubject, context, productbgtcls monthAgentName, authorname authorName, authoridnumber authorIdnumber, taxpayeridnumber taxpayerIdnumber, authorcompany authorCompany, IF( authortype = 1, '公司内部', '公司外部' ) authorType, authorprovince authorProvince, authorcity authorCity, bankaccountbranchname openBank, bankaccount bankAccount, paperquality paperQuality, pageorcopy pageOrCopy, feestandard feeStandard, copefee copeFee, concat(empname,'(',empno,')') empname, feebdgdept feebdgDept, businessgroup businessGroup, needzz FROM budget_authorfeedetail
authorfeesumid = #{params.feeSumId}
and taxtype = #{params.taxType}
and productbgtcls like concat('%',#{params.agentName},'%')
and authortype = #{params.authorType}
and (authorname like concat('%',#{params.author},'%') )
order by empno

SELECT bad.yearperiod, bad.feemonth, IF ( bad.authortype = 1, '是', '否' ) authortype, bad.authorname, bad.authoridnumber, bad.taxpayeridnumber, bad.authorcompany, bad.authorprovince, bad.authorcity, bad.bankaccount, bad.bankaccountbranchname, bad.feestandard, bad.copefee, bad.taxtype, bad.tax, bad.agentname, bad.reimbursesubject, bad.producttype, bad.productbgtcls, bad.empno, bad.empname, bad.`subject`, bad.context, bad.paperquality, bad.pageorcopy, bad.feebdgdept, bad.businessgroup, IF ( bad.needzz = 1, '是', '否' ) needzz FROM budget_authorfeesum baf LEFT JOIN budget_authorfeedetail bad ON baf.id = bad.authorfeesumid WHERE 1=1
and baf.yearperiod = #{period}
and baf.feemonth = #{feeMonth}

