SELECT ba.id, ba.code, ba.pname pname, ba.accountname accountName, ba.accounttype accountType, ba.bankaccount bankAccount, ba.wagesflag wagesFlag, ba.branchcode branchCode, ba.orderno orderNo, ba.stopflag stopFlag, ba.remark, ba.outKey, wb.province, wb.city, wb.bank_name bankName, wb.sub_branch_name branchName, wp.DEPT_ID deptId, ba.update_time, ba.update_by,ba.outkey outKey FROM budget_bank_account ba LEFT JOIN wb_banks wb ON ba.branchcode = wb.sub_branch_code LEFT JOIN wb_person wp ON ba.code = wp.PERSON_CODE WHERE 1 = 1
and ba.code like CONCAT ('%', #{value}, '%')
and wp.DEPT_ID in (${value})
and ba.pname like CONCAT ('%', #{value}, '%')
and ba.accounttype = #{value}
and ba.bankaccount like CONCAT ('%', #{value}, '%')
and ba.wagesflag = #{value}
and ba.branchcode = #{value}
and ba.stopflag = #{value}
and (ba.accountname LIKE CONCAT('%', #{value}, '%') or ba.code LIKE CONCAT('%', #{value}, '%') or ba.bankaccount LIKE CONCAT('%', #{value}, '%'))
and ba.outkey is not null or ba.outkey != ''
and ba.outkey is null or ba.outkey = ''
order by ba.orderno
