mapper.select:
SELECT blm.id, blm.lendmoneycode AS lendMoneyCode, blm.empno AS empNo, blm.empname AS empName, blm.lendtype AS lendType, IF(blm.lendtype = 15, '合同借款', '非合同借款') AS lendTypeDesc, blm.lendmoney AS lendMoney, blm.repaidmoney AS repaidMoney, (blm.lendmoney - blm.repaidmoney) AS unRepaidMoney, blm.lenddate AS lendDate, blm.planpaydate AS payPlanDate, blm.createtime AS createDate, blm.remark, IF((blm.lendmoney - blm.repaidmoney) > 0, 0, 1) AS repaymentStatus, bc.contractname AS contractName, bc.agreesumtype AS agreeSumType, bp.paymoneystatus AS payMoneyStatus, bp.paytype AS payType FROM budget_lendmoney_new blm left JOIN budget_contract bc ON blm.contractid = bc.id left JOIN budget_paymoney bp ON blm.id = bp.paymoneyobjectid WHERE blm.deleteflag = 0
and (blm.empname like CONCAT('%', #{paramMap.name}, '%') or blm.empno like CONCAT('%', #{paramMap.name}, '%'))
and IF((blm.lendmoney - blm.repaidmoney) > 0, 0, 1) = #{paramMap.repaymentStatus}
and bp.paymoneystatus = #{paramMap.payMoneyStatus}
and blm.lendDate = #{paramMap.lendDate}
and blm.lendtype = #{paramMap.lendType}
and (blm.lendtype = 15 or blm.lendtype = 16)
ORDER BY blm.createtime DESC
