mapper.select:
SELECT beat.id, bes.`status`, bes.id sumId, beat.extract_code 'code', byp.period yearPeriod, concat( substr( bes.extractmonth, 5, 2 )+ 0, '月' ) monthPeriod, bes.extractmonth extractBatch, bes.deptname unitName, beat.create_time createTime, CONCAT( wu.DISPLAY_NAME, '(', wu.USER_NAME, ')' ) accountant, beat.accountant_time accountantTime, ( SELECT sum( pay_money ) FROM budget_extract_per_pay_detail WHERE extract_code = beat.extract_code AND pay_status = beat.task_type and billing_unit_id=bbu.id) payMoney, beat.task_type taskType, bbu.`name` billingUnitName FROM budget_extract_account_task beat LEFT JOIN budget_extractsum bes ON ifnull(beat.relation_extract_code,beat.extract_code) = bes.`code` LEFT JOIN budget_year_period byp ON byp.id = bes.yearid LEFT JOIN wb_user wu ON beat.accountant_emp_no = wu.USER_NAME LEFT JOIN budget_billing_unit bbu ON bbu.id = beat.billing_unit_id
beat.is_should_account = 1 and beat.accountant_status = 1
and bes.`code` like concat('%',#{params.orderNo},'%')
and byp.id = #{params.yearPeriodId}
and (substr( bes.extractmonth, 5, 2 )+ 0) = #{params.monthPeriodId}
and bes.extractmonth like concat('%',#{params.extractBatch},'%')
and bes.deptname like concat('%',#{params.deptName},'%')
and bbu.`name` like concat('%',#{params.billingUnitName},'%')
and bes.status = #{params.status}
order by beat.create_time desc

SELECT beppd.receiver_code empNo, beppd.receiver_name empName, beppd.receiver_account_name accountName, beppd.pay_money money, beppd.receiver_account_name receiver, beppd.receiver_open_bank receiverBank, beppd.receiver_bank_account bankAccount, bbu.`name` unitName, beppd.pay_status payStatus, beppd.personality_id personalityId, bief.account_type accountType FROM budget_extract_per_pay_detail beppd LEFT JOIN budget_billing_unit bbu ON bbu.id = beppd.billing_unit_id left join budget_individual_employee_files bief on bief.id = beppd.personality_id where beppd.extract_code = #{params.extractCode}
and (beppd.receiver_code like concat('%',#{params.empNo},'%') or beppd.receiver_name like concat('%',#{params.empNo},'%'))
and bbu.`name` like concat('%',#{params.unitName},'%')
and beppd.receiver_account_name like concat('%',#{params.personalityName},'%')
and beppd.pay_status = #{params.payStatus}
and beppd.personality_id in
#{id}
and beppd.pay_status = 3
