mapper.select:
SELECT bei.id, bei.yearid, byp.period, bei.iscompanyemp, bei.isbaddebt, bei.empid, bei.empno, bei.empname, bei.idnumber, bei.copeextract, bei.consotax, bei.extract_type extractType, ifnull(bei.should_send_extract,0) shouldSendExtract, ifnull(bei.tax,0) tax, ifnull(bei.tax_reduction,0) taxReduction, ifnull(bei.invoice_excess_tax,0) invoiceExcessTax, ifnull(bei.invoice_excess_tax_reduction,0) invoiceExcessTaxReduction FROM budget_extract_importdetail bei LEFT JOIN budget_year_period byp ON bei.yearid = byp.id
extractsumid = #{params.sumId}
and bei.yearid = #{params.yearid}
and bei.iscompanyemp = #{params.iscompanyemp}
and bei.isbaddebt = #{params.isbaddebt}
and ( bei.empno like concat('%',#{params.empno},'%') or bei.empname like concat('%',#{params.empno},'%') )
and bei.idnumber like concat('%',#{params.idnumber},'%')
order by bei.updatetime desc

select * from (
select bei.* from budget_extractsum be
left join budget_extract_importdetail bei on be.id = bei.extractsumid
where bei.if_big_manager != -1
and bei.createtime >= date_sub(now(), interval 3 month) and be.deptid = #{deptId}

union
select bei.* from budget_extractsum be
left join budget_extract_importdetail bei on be.id = bei.extractsumid
where bei.if_big_manager = 1 and be.deptid = #{deptId}

) temp order by temp.createtime desc

select * from (
select bei.* from budget_extractsum be
left join budget_extract_importdetail bei on be.id = bei.extractsumid
where bei.if_manager != -1
and bei.createtime >= date_sub(now(), interval 3 month) and bei.empno = #{empNo}

union
select bei.* from budget_extractsum be
left join budget_extract_importdetail bei on be.id = bei.extractsumid
where bei.if_manager = 1 and bei.empno = #{empNo}

    ) temp order by temp.createtime desc
