mapper.select:
SELECT brd.repaymoneyid, DATE_FORMAT( brd.createtime, '%Y-%m-%d %H:%i:%S' ) repaytime, brd.repaymoney, brm.empno, brm.empname, bl.lendtype, brd.curlendmoney, bl.lendmoneycode, brd.nowlendmoney FROM budget_repaymoney_detail_new brd LEFT JOIN budget_repaymoney_new brm on brd.repaymoneyid = brm.id left JOIN budget_lendmoney_new bl on brd.lendmoneyid = bl.id
brd.repaymoneyid = #{params.repaymoneyid}
and bl.lendtype = #{params.lendtype}
and bl.lendmoneycode like concat('%',#{params.lendmoneycode},'%')
order by brd.createtime desc


SELECT ifnull(bep.paymoney1,0) paymoney1 FROM budget_extractpayment bep,budget_extractpaydetail bepd   

	bepd.id = bep.budgetextractpaydetailid 
	and bepd.idnumber=#{idnumber} 
	and bep.bunitid1 = #{billingUnitId} 
	and bepd.extractmonth < #{curExtractBatch} 
	and bepd.extractmonth >= #{curYearStartExtractBatch}
