mapper.select:
SELECT _subject.name AS subjectName, _monthagent.name, _monthagent.total, _monthagent.monthbusiness, monthPeriod.period, _yearagent.total + _yearagent.addmoney + _yearagent.lendinmoney - _yearagent.lendoutmoney - _yearagent.executemoney AS unExecute FROM budget_month_agent _monthagent INNER JOIN budget_year_agent _yearagent ON _yearagent.id = _monthagent.yearagentid INNER JOIN budget_subject _subject ON _subject.yearid = _monthagent.yearid ANd _subject.id = _monthagent.subjectid INNER JOIN budget_month_period monthPeriod ON monthPeriod.id = _monthagent.monthid WHERE _subject.leafflag = 1 AND _monthagent.unitid = #{budgetUnitId} AND _monthagent.monthid = #{monthId}
AND formulaflag != 1 AND costsplitflag != 1 AND jointproductflag != 1
AND formulaflag != 1 AND costsplitflag != 1 and jointproductflag = 1

SELECT unit.id AS unitId, unit.name AS unitName, subject.id AS subjectId, subject.name AS subjectName, bma.id AS agentId, bma.name AS agentName FROM budget_subject AS subject INNER JOIN budget_unit_subject AS unitsubject ON unitsubject.subjectid = subject.id INNER JOIN budget_unit unit ON unitsubject.unitid = unit.id INNER JOIN budget_month_agent bma on unit.id = bma.unitid and bma.subjectid = subject.id WHERE formulaflag != 1 and unit.yearId = #{map.yearId} and bma.monthid = #{map.monthId}
and unit.id = #{map.budgetUnitId}
AND ( unit.baseunitid IN (SELECT _unit.baseunitid FROM budget_unit _unit WHERE _unit.managers is NOT NULL AND CONCAT(',',_unit.managers,',') LIKE CONCAT('%,', #{map.userId}, ',%') ) or (1=1 ${map.authSql}) )
and ( subject.name like concat('%',#{map.name},'%') OR bma.name like concat('%',#{map.name},'%') )
and ( unit.name like concat('%',#{map.name},'%') OR subject.name like concat('%',#{map.name},'%') OR bma.name like concat('%',#{map.name},'%') )
order by unit.id, subject.id, bma.id

SELECT id,name
FROM budget_month_agent
WHERE yearid = #{map.yearId} AND unitid = #{map.unitId} AND subjectid = #{map.subjectId} AND monthid = #{map.monthId}
