mapper.select:
SELECT subject.id AS id, subject.parentid AS parentId, subject.code AS subjectCode, subject.leafflag AS leaf, subject.`name` AS subjectName, monthSubject.total AS monthMoney, monthSubject.monthbusiness AS monthBusiness, yearSubject.total AS yearMoney, monthSubject.addmoney+monthSubject.lendinmoney-monthSubject.lendoutmoney AS yearAddMoney, monthSubject.lendinmoney AS yearLendInMoney, monthSubject.lendoutmoney AS yearLendOutMoney, t.sumMoney AS yearExecuteMoney, yearSubject.revenueformula AS yearRevenueFormula, unit.id AS unitId, unit.name AS unitName, yearPeriod.period AS yearPeriod, yearPeriod.code AS yearCode, monthPeriod.id AS monthId, monthPeriod.period AS monthPeriod FROM budget_month_subject AS monthSubject INNER JOIN budget_subject subject ON monthSubject.subjectid = subject.id INNER JOIN budget_year_subject yearSubject ON yearSubject.unitid = monthSubject.unitid and yearSubject.subjectid = monthSubject.subjectid INNER JOIN budget_unit unit ON unit.id = monthSubject.unitid INNER JOIN budget_year_period yearPeriod on yearPeriod.id = monthSubject.yearid INNER JOIN budget_month_period monthPeriod ON monthPeriod.id = monthSubject.monthid inner join (select yearid,unitid,subjectid,sum(executemoney) sumMoney from budget_month_subject where monthid in
#{id}
group by yearid,unitid,subjectid) t on monthSubject.unitid = t.unitid and monthSubject.yearid = t.yearid and monthSubject.subjectid = t.subjectid where monthSubject.unitid = #{budgetUnitId} and monthSubject.monthid = #{monthId}
