mapper.select:
SELECT detail_.*
FROM budget_reimbursementorder_detail detail_
INNER JOIN budget_reimbursementorder order_ ON detail_.reimbursementid = order_.id
WHERE detail_.monthagentid IN (SELECT id
FROM budget_month_agent
WHERE unitid = #{unitId}
AND subjectid = #{subjectId}

    AND monthid = #{monthId}

)
AND detail_.reimflag = 1
AND order_.reuqeststatus = 1

SELECT myttt.* FROM ( SELECT 1unit_.name bxunitname, order_.applicantid, order_.bxtype, order_.reimcode, order_.reuqeststatus , (CASE order_.reuqeststatus WHEN -1 THEN "退回" WHEN 0 THEN "草稿" WHEN 1 THEN "审核中" WHEN 2 THEN "审核通过" ELSE order_.reuqeststatus END) AS reuqeststatusText, order_.reimperonsname bxr, detail_.reimbursementid, detail_.reimmoney, detail_.monthagentname, detail_.remark, detail_.subjectname, detail_.reimflag, '报销' reimflagtype, unit_.baseunitid, detail_.monthagentmoney, detail_.monthagentunmoney, detail_.yearagentmoney, detail_.yearagentunmoney, month_.id monthid, month_.period monthname, year_.id yearid, year_.period yearname, unit_.`name` unitname, DATE_FORMAT(order_.applicanttime,'%Y-%m-%d') applicanttime FROM budget_reimbursementorder_detail AS detail_ INNER JOIN budget_reimbursementorder AS order_ ON detail_.reimbursementid = order_.id INNER JOIN budget_month_agent _agent ON detail_.monthagentid = _agent.id INNER JOIN budget_year_period AS year_ ON _agent.yearid = year_.id INNER JOIN budget_month_period AS month_ ON _agent.monthid = month_.id INNER JOIN budget_unit AS unit_ ON unit_.yearid = year_.id AND _agent.unitid = unit_.id INNER JOIN budget_unit AS 1unit_ ON 1unit_.yearid = order_.yearid AND order_.unitid = 1unit_.id union all SELECT 1unit_.name bxunitname, order_.applicantid, order_.bxtype, order_.reimcode, order_.reuqeststatus , (CASE order_.reuqeststatus WHEN -1 THEN "退回" WHEN 0 THEN "草稿" WHEN 1 THEN "审核中" WHEN 2 THEN "审核通过" ELSE order_.reuqeststatus END) AS reuqeststatusText, order_.reimperonsname 'bxr', detail_.reimbursementid, detail_.allocatedmoney reimmoney, detail_.monthagentname, detail_.remark, detail_.subjectname, detail_.reimflag, '划拨' reimflagtype, unit_.baseunitid, detail_.monthagentmoney, detail_.monthagentunmoney, detail_.yearagentmoney, detail_.yearagentunmoney, month_.id AS monthid, month_.period AS monthname, year_.id AS yearid, year_.period AS yearname, unit_.`name` AS unitname, DATE_FORMAT(order_.applicanttime,'%Y-%m-%d') applicanttime FROM budget_reimbursementorder_allocated AS detail_ INNER JOIN budget_reimbursementorder AS order_ ON detail_.reimbursementid = order_.id INNER JOIN budget_month_agent _agent ON detail_.monthagentid = _agent.id INNER JOIN budget_year_period AS year_ ON _agent.yearid = year_.id INNER JOIN budget_month_period AS month_ ON _agent.monthid = month_.id INNER JOIN budget_unit AS unit_ ON unit_.yearid = year_.id AND _agent.unitid = unit_.id INNER JOIN budget_unit AS 1unit_ ON 1unit_.yearid = order_.yearid AND order_.unitid = 1unit_.id ) myttt WHERE 1=1
AND myttt.applicantid = #{value}
AND (myttt.baseunitid IN ( SELECT DISTINCT _unit.baseunitid FROM budget_unit _unit WHERE _unit.managers is NOT NULL AND CONCAT(",",_unit.managers,",") LIKE CONCAT('%', #{value}, '%') )
or (1=1 ${authSql})
)
AND myttt.reuqeststatus in
#{statusId}
AND myttt.reimcode LIKE CONCAT('%', #{value}, '%')
AND myttt.yearid in
#{year}
AND myttt.monthid in
#{month}
AND myttt.bxr LIKE CONCAT('%', #{value}, '%')
AND myttt.subjectname LIKE CONCAT('%', #{value}, '%')
AND myttt.reimmoney = #{value}
AND myttt.reimflag = #{value}
AND myttt.reimflagtype = #{value}
AND myttt.bxunitname LIKE CONCAT('%', #{value}, '%')
AND myttt.unitname LIKE CONCAT('%', #{value}, '%')
AND myttt.monthagentname LIKE CONCAT('%', #{value}, '%')
AND myttt.remark LIKE CONCAT('%', #{value}, '%')
AND myttt.reimmoney >= #{value}
AND myttt.reimmoney <= #{value}
ORDER BY myttt.reimcode desc

SELECT myttt.* FROM ( SELECT 1unit_.name bxunitname, order_.applicantid, order_.bxtype, order_.reimcode, order_.reuqeststatus , (CASE order_.reuqeststatus WHEN -1 THEN "退回" WHEN 0 THEN "草稿" WHEN 1 THEN "审核中" WHEN 2 THEN "审核通过" ELSE order_.reuqeststatus END) 'reuqeststatusText', order_.reimperonsname bxr, detail_.reimbursementid, detail_.reimmoney, detail_.monthagentname, detail_.remark, detail_.subjectname, detail_.reimflag, '报销' reimflagtype, unit_.baseunitid, detail_.monthagentmoney, detail_.monthagentunmoney, detail_.yearagentmoney, detail_.yearagentunmoney, month_.id monthid, month_.period monthname, year_.id yearid, year_.period yearname, unit_.`name` unitname, detail_.bunitname, b.payunitname, DATE_FORMAT(order_.applicanttime,'%Y-%m-%d') applicanttime FROM budget_reimbursementorder_detail AS detail_ INNER JOIN budget_reimbursementorder AS order_ ON detail_.reimbursementid = order_.id INNER JOIN budget_month_agent _agent ON detail_.monthagentid = _agent.id INNER JOIN budget_year_period AS year_ ON _agent.yearid = year_.id INNER JOIN budget_month_period AS month_ ON _agent.monthid = month_.id INNER JOIN budget_unit AS unit_ ON unit_.yearid = year_.id AND _agent.unitid = unit_.id INNER JOIN budget_unit AS 1unit_ ON 1unit_.yearid = order_.yearid AND order_.unitid = 1unit_.id left JOIN (select brt.reimbursementid,GROUP_CONCAT(brt.draweeunitname) payunitname from budget_reimbursementorder_trans brt GROUP BY brt.reimbursementid ) b on b.reimbursementid = order_.id union all SELECT 1unit_.name bxunitname, order_.applicantid, order_.bxtype, order_.reimcode, order_.reuqeststatus , (CASE order_.reuqeststatus WHEN -1 THEN "退回" WHEN 0 THEN "草稿" WHEN 1 THEN "审核中" WHEN 2 THEN "审核通过" ELSE order_.reuqeststatus END) 'reuqeststatusText', order_.reimperonsname 'bxr', detail_.reimbursementid, detail_.allocatedmoney reimmoney, detail_.monthagentname, detail_.remark, detail_.subjectname, detail_.reimflag, '划拨' reimflagtype, unit_.baseunitid, detail_.monthagentmoney, detail_.monthagentunmoney, detail_.yearagentmoney, detail_.yearagentunmoney, month_.id AS monthid, month_.period AS monthname, year_.id AS yearid, year_.period AS yearname, unit_.`name` AS unitname, '' bunitname, b.payunitname, DATE_FORMAT(order_.applicanttime,'%Y-%m-%d') applicanttime FROM budget_reimbursementorder_allocated AS detail_ INNER JOIN budget_reimbursementorder AS order_ ON detail_.reimbursementid = order_.id INNER JOIN budget_month_agent _agent ON detail_.monthagentid = _agent.id INNER JOIN budget_year_period AS year_ ON _agent.yearid = year_.id INNER JOIN budget_month_period AS month_ ON _agent.monthid = month_.id INNER JOIN budget_unit AS unit_ ON unit_.yearid = year_.id AND _agent.unitid = unit_.id INNER JOIN budget_unit AS 1unit_ ON 1unit_.yearid = order_.yearid AND order_.unitid = 1unit_.id left JOIN (select brt.reimbursementid,GROUP_CONCAT(brt.draweeunitname) payunitname from budget_reimbursementorder_trans brt GROUP BY brt.reimbursementid ) b on b.reimbursementid = order_.id ) myttt WHERE 1=1
AND myttt.applicantid = #{value}
AND (myttt.baseunitid IN ( SELECT DISTINCT _unit.baseunitid FROM budget_unit _unit WHERE _unit.managers is NOT NULL AND CONCAT(",",_unit.managers,",") LIKE CONCAT('%', #{value}, '%') )
or (1=1 ${authSql})
)
AND myttt.reuqeststatus in
#{statusId}
AND myttt.reimcode LIKE CONCAT('%', #{value}, '%')
AND myttt.yearid in
#{year}
AND myttt.monthid in
#{month}
AND myttt.bxr LIKE CONCAT('%', #{value}, '%')
AND myttt.subjectname LIKE CONCAT('%', #{value}, '%')
AND myttt.reimmoney = #{value}
AND myttt.reimflag = #{value}
AND myttt.reimflagtype = #{value}
AND myttt.bxunitname LIKE CONCAT('%', #{value}, '%')
AND myttt.unitname LIKE CONCAT('%', #{value}, '%')
AND myttt.monthagentname LIKE CONCAT('%', #{value}, '%')
AND myttt.remark LIKE CONCAT('%', #{value}, '%')
ORDER BY myttt.reimcode desc
