       SELECT ttt.* 
       FROM
       ( 
       SELECT `order`.qrcodebase64str qrcode, `order`.id, `order`.reimcode, order.reuqeststatus , order.reuqeststatus reuqeststatus_dictname, `order`.yearid,  myyear.period AS `yearname`, `order`.unitid , budgetunit.name ysdw, `order`.monthid, mymonth.period AS `monthname`, `order`.reimperonsid, CONCAT(`order`.reimperonsname,'(', user.USER_NAME, ')') 'bxr', DATE_FORMAT(`order`.reimdate,'%Y-%m-%d') bxrq, `order`.reimmoney bxje, `order`.nonreimmoney, `order`.paymentmoney czje, `order`.transmoney zzje, `order`.cashmoney xjje, `order`.othermoney, `order`.allocatedmoney hbje, `order`.attachcount fjzs, `order`.remark, budgetunit.baseunitid, `order`.createtime, `order`.updatetime, `order`.applicantid, `order`.bxtype, `order`.bxtype bxtype_dictname, `order`.applicantame, `order`.traveler, `order`.travelreason, `order`.curscanstatusname, `order`.curscanername, DATE_FORMAT(`order`.verifytime,'%Y-%m-%d %H:%i:%S') verifytime, DATE_FORMAT(`order`.submittime,'%Y-%m-%d %H:%i:%S') submittime, DATE_FORMAT(`order`.applicanttime,'%Y-%m-%d %H:%i:%S') applicanttime FROM budget_reimbursementorder AS `order` INNER JOIN budget_year_period AS myyear ON `order`.yearid = myyear.id INNER JOIN budget_month_period AS mymonth ON `order`.monthid = mymonth.id INNER JOIN budget_unit AS budgetunit ON budgetunit.yearid = myyear.id AND `order`.unitid = budgetunit.id  LEFT JOIN wb_user AS user ON `order`.reimperonsid = user.USER_ID 
	   ) ttt
	   WHERE 1 = 1



					AND ttt.applicantid = #{value}




                	AND ( ttt.baseunitid IN (SELECT _unit.baseunitid FROM budget_unit _unit WHERE _unit.managers is NOT NULL AND CONCAT(',',_unit.managers,',') LIKE CONCAT('%,', #{value}, ',%') )             	

				    	or (1=1 ${authSql})

				    )




                    AND ttt.reuqeststatus = #{value}




                    AND ttt.reimcode LIKE CONCAT('%', #{value}, '%')  




                    AND ttt.yearid = #{value}




                    AND ttt.monthid = #{value}




                    AND ttt.bxr LIKE CONCAT('%', #{value}, '%')  




                    AND ttt.bxrq = #{value} 




                    AND ttt.bxje = #{value}  




                    AND ttt.czje = #{value}




                    AND ttt.zzje = #{value} 




                    AND ttt.xjje = #{value} 




                    AND ttt.hbje = #{value} 




                    AND ttt.othermoney = #{value} 




