SELECT ttt.* FROM ( SELECT `order`.id, `order`.id orderid, `order`.reimcode, `order`.curscanstatusname, `order`.curscanername, `order`.yearid, myyear.period yearname, `order`.unitid, budgetunit.name ysdw, `order`.monthid , mymonth.period monthname, `order`.reimperonsname bxr, DATE_FORMAT(`order`.reimdate,'%Y-%m-%d') bxrq, `order`.reimmoney bxje, `order`.nonreimmoney, `order`.paymentmoney czje, `order`.transmoney zzje, `order`.cashmoney xjje, `order`.allocatedmoney hbje, `order`.attachcount fjzs, `order`.remark, `order`.curscaner, `order`.createtime, `order`.updatetime, `order`.applicantid, `order`.bxtype, `order`.bxtype bxtype_dictname, `order`.applicantame, `order`.traveler, `order`.travelreason, `order`.reuqeststatus, order.receivestatus, order.curscantime, order.parverifystatus, order.budgetverifystatus, order.budgetverifyreceivestatus, order.requestlevel, order.fdstatus, order.fdreceivestatus, order.accountstatus, order.accountreceivestatus, order.parverifyreceivestatus, order.cashierpaymentreceivestatus, order.account1receivestatus, order.voucherauditreceivestatus, order.drawbillreceivestatus, order.endreceivestatus, order.financialmanagestatus, order.financialmanagereceivestatus, order.generalmanagestatus, order.generalmanagereceivestatus, DATE_FORMAT(order.curscantime ,'%Y-%m-%d %H:%i:%S') receivetime, DATE_FORMAT(`order`.applicanttime,'%Y-%m-%d') applicanttime, DATE_FORMAT(`order`.submittime,'%Y-%m-%d %H:%i:%S') submittime, order.work_flow_step workFlowStep FROM budget_reimbursementorder AS `order` INNER JOIN budget_year_period AS myyear ON `order`.yearid = myyear.id INNER JOIN budget_month_period AS mymonth ON `order`.monthid = mymonth.id INNER JOIN budget_unit AS budgetunit ON budgetunit.yearid = myyear.id AND `order`.unitid = budgetunit.id ) ttt WHERE ttt.reuqeststatus > 0
AND ttt.curscaner = #{curscaner}
AND ttt.reimcode LIKE CONCAT('%', #{reimcode}, '%')
AND ttt.bxje = #{reimmoney}
${whereSql}
ORDER BY ttt.requestlevel desc , ttt.submittime desc
${authSql}

SELECT _detail.reimcode, _detail.fdername, _detail.fder, _detail.bunitname, _detail.planbunitname, _detail.receivername, _detail.accountstatus, _detail.accountername, _order.curscanername, DATE_FORMAT(_detail.fdtime, '%Y-%m-%d %H:%i:%S') fdtime, DATE_FORMAT(_detail.receivetime, '%Y-%m-%d %H:%i:%S') receivetime, DATE_FORMAT(_detail.accounttime, '%Y-%m-%d %H:%i:%S') accounttime FROM budget_reimbursementorder_fdtask_detail _detail LEFT JOIN budget_reimbursementorder _order ON _order.id = _detail.reimbursementid WHERE _detail.accountstatus = 0 AND ((_detail.planaccounters IS NOT NULL AND CONCAT(',',_detail.planaccounters,',') LIKE CONCAT('%', #{empNo}, '%') )
)
AND _detail.reimcode LIKE CONCAT('%', #{reimcode}, '%')
ORDER BY _detail.fdtime desc
${authSql}

SELECT byp.period, concat(bro.monthid,'月') monthName, bu.`name` unitName, convert(bro.reimmoney,decimal(10,2)) reimmoney, bro.reimcode, bro.reimperonsname, brv.verifyinfo, bro.id, bro.subjectNames FROM ( SELECT ord.id, ord.reimcode, ord.reimperonsname, ord.yearid, ord.monthid, ord.unitid, GROUP_CONCAT(detail.subjectname) subjectNames, ord.reimmoney FROM budget_reimbursementorder ord INNER JOIN budget_reimbursementorder_detail detail ON ord.id = detail.reimbursementid WHERE ord.reuqeststatus = 2
AND ord.yearid = #{yearId}
AND ord.monthid = #{monthId}
GROUP BY ord.id ORDER BY ord.id )bro INNER JOIN budget_reimbursementorder_verifylog brv ON bro.id = brv.reimbursementid LEFT JOIN budget_year_period byp ON byp.id = bro.yearid LEFT JOIN budget_unit bu on bu.id = bro.unitid WHERE brv.backtype IN (1, 2)
