mapper.select:
SELECT subject.costaddflag, if(subject.costaddflag=0,'否','是') costaddflagstr, subject.formulaorderno, subject.formula, subject.formulaflag, if(subject.formulaflag=0,'否','是') formulaflagstr, subject.parentid, subject.stopflag,if(subject.stopflag=0,'否','是') stopflagstr, subject.remark, subject.orderno, subject.code, subject.yearid, subject.name, subject.subjectid, subject.id, bsubject.name basename, year.period yearname, subject.yearplantype, subject.assistflag, if(subject.assistflag=0,'否','是') assistflagstr, subject.upsumflag, subject.level, if(subject.upsumflag=0,'否','是') upsumflagstr, subject.costsplitflag, if(subject.costsplitflag=0,'否','是') costsplitflagstr, subject.costlendflag, if(subject.costlendflag=0,'否','是') costlendflagstr, subject.jointproductflag, if(subject.jointproductflag=0,'否','是') jointproductflagstr, subject.procategoryid, subject.jindiecode, subject.jindiename FROM budget_subject AS subject INNER JOIN budget_base_subject AS bsubject ON bsubject.id = subject.subjectid INNER JOIN budget_year_period AS year ON year.id = subject.yearid WHERE 1=1 AND subject.yearid = #{yearId}
and subject.name LIKE CONCAT('%', #{subName}, '%')
and subject.stopflag = #{stopFlag}
ORDER BY subject.level, subject.parentid, subject.orderno

    	SELECT sub.name, sub.code, sub.jindiecode, period.period FROM budget_subject sub LEFT JOIN budget_year_period period ON sub.yearid = period.id 
		WHERE yearid = #{yearId}
		ORDER BY sub.orderno
