mapper.select:
SELECT count(0)
FROM budget_unit _sunit
 INNER
         JOIN budget_unit _punit ON _punit.id = _sunit.parentid
WHERE _punit.baseunitid = #{baseUnitParentId}
  AND _sunit.baseunitid != #{baseUnitId}
  AND _sunit.id = #{unitId}

SELECT bu.id id, bu.baseunitid baseUnitId, baseunit.name baseUnitName, bu.name, bu.yearid yearId, yp.period yearName, bu.parentid parentId, if(bu.budgetflag, 1, 0) budgetFlag, bu.pids, bu.managers, bu.budgetdepts budgetDepts, bu.budgetusers budgetUsers, bu.orderno orderNo, bu.unittype unitType, bu.remark, bu.ccratioformula ccratioFormula, bu.revenueformula revenueFormula FROM budget_unit bu INNER JOIN budget_year_period yp ON bu.yearid = yp.id LEFT JOIN budget_base_unit baseunit ON bu.baseunitid = baseunit.id WHERE bu.yearid = #{yearId}
and bu.name LIKE CONCAT('%', #{unitName}, '%')
AND ( bu.baseunitid IN (SELECT _unit.baseunitid FROM budget_unit _unit WHERE _unit.managers is NOT NULL AND CONCAT(',',_unit.managers,',') LIKE CONCAT('%,', #{userId}, ',%') )
or (1=1 ${authSql})
) ORDER BY bu.orderno

SELECT bu.id id, bu.baseunitid baseUnitId, baseunit.name baseUnitName, bu.name, bu.yearid yearId, yp.period yearName, bu.parentid parentId, if(bu.budgetflag, 1, 0) budgetFlag, bu.pids, bu.managers, bu.budgetdepts budgetDepts, bu.budgetusers budgetUsers, bu.orderno orderNo, bu.unittype unitType, bu.remark, bu.ccratioformula ccratioFormula, bu.revenueformula revenueFormula FROM budget_unit bu INNER JOIN budget_year_period yp ON bu.yearid = yp.id LEFT JOIN budget_base_unit baseunit ON bu.baseunitid = baseunit.id WHERE bu.yearid = #{yearId}
and bu.name LIKE CONCAT('%', #{unitName}, '%')
ORDER BY bu.orderno

SELECT yearperiod.period AS yearPeriod, unit.id, unit.yearid, unit.`name`, unit.submittime, unit.submitorid, unit.submitorname, unit.verifytime, unit.verifyorid, unit.verifyorname, unit.verifystr, unit.requeststatus, unit.submitflag, unit.baseunitid FROM budget_unit unit INNER JOIN budget_year_period yearperiod ON unit.yearid = yearperiod.id where unit.id not in ( select distinct parentid from budget_unit where 1 = 1
and yearid = #{paramMap.yearId}
)
and unit.yearid = #{paramMap.yearId}
and unit.name LIKE CONCAT('%', #{paramMap.name}, '%')

		SELECT DISTINCT _unitsubject.subjectid FROM budget_unit_subject _unitsubject INNER JOIN budget_unit _unit ON _unitsubject.unitid = _unit.id WHERE
		(_unit.parentid = #{parentId}

            OR _unit.id = #{unitId}

		) AND _unit.yearid = #{yearId}
