mapper.select:
SELECT agent.*, _unit.requeststatus AS requestStatus , _unit.name AS unitName , product.name AS productName, budget_product_category.name AS catename, basesubject.id AS baseSubjectId , basesubject.name AS baseSubjectName FROM budget_year_agent AS agent INNER JOIN budget_unit AS _unit ON _unit.yearid = agent.yearid AND _unit.id = agent.unitid INNER JOIN budget_subject AS mysubject ON mysubject.id = agent.subjectid LEFT JOIN budget_product AS product ON product.id = agent.productid LEFT JOIN budget_product_category ON product.procategoryid = budget_product_category.id LEFT JOIN budget_base_subject basesubject ON basesubject.id = agent.budgetsubjectid WHERE mysubject.id = #{budgetSubjectId}
AND agent.unitid = #{budgetUnitId}
AND agent.name LIKE concat('%',#{name},'%')
order by agent.createtime desc

SELECT y.*
FROM budget_year_agent y
         INNER JOIN budget_unit _unit ON _unit.yearId = y.yearId AND _unit.id = y.unitId
WHERE _unit.yearId = #{yearId}
  AND (_unit.id = #{budgetUnitId}
   or _unit.parentid = #{budgetUnitId})

select count(*) 
FROM  budget_year_agent _agent INNER JOIN budget_unit _unit ON _agent.yearid = _unit.yearid AND _agent.unitid = _unit.id
where _agent.yearid = #{yearId} 
and _agent.subjectid = #{subjectId}

and _agent.productid = #{productId}

and _unit.requeststatus > 0

select ya.*
from budget_year_agent ya,
     budget_month_agent ma
where ma.yearagentid = ya.id
  and ma.id = #{monthAgentId}
