mapper.select:
SELECT agentadd.id, agentadd.requeststatus AS requestStatus, agentadd.yearaddcode AS yearAddCode, year_.id AS yearId, year_.period AS yearPeriod, unit.id AS budgetUnitId, unit.`name` AS budgetUnitName, agentadd.total AS addMoney, agentadd.creatorname AS creatorName, agentadd.fileoriginname AS fileOriginName, agentadd.fileurl AS fileUrl, agentadd.oapassword AS oaPassword, agentadd.createtime AS createDate, agentadd.is_exempt_fine as isExemptFine, agentadd.exempt_fine_reason as exemptFineReason FROM budget_year_agentaddinfo AS agentadd INNER JOIN budget_year_period AS year_ ON agentadd.yearid = year_.id INNER JOIN budget_unit AS unit ON unit.yearid = year_.id AND agentadd.unitid = unit.id
and agentadd.yearid = #{map.yearId}
and unit.`name` like concat('%',#{map.name},'%')
and agentadd.yearaddcode like concat('%',#{map.yearAddCode},'%')
and agentadd.requeststatus = #{map.requestStatus}
AND ( unit.baseunitid IN (SELECT _unit.baseunitid FROM budget_unit _unit WHERE _unit.managers is NOT NULL AND CONCAT(',',_unit.managers,',') LIKE CONCAT('%,', #{map.userId}, ',%') )
or (1=1 ${map.authSql})
)
order by agentadd.createtime desc

select addinfo.*, period.period, unit.`name` as unitName from budget_year_agentaddinfo addinfo INNER JOIN budget_year_period AS period ON addinfo.yearid = period.id INNER JOIN budget_unit AS unit ON unit.yearid = period.id AND addinfo.unitid = unit.id
and addinfo.yearid = #{yearId}
and unit.`name` like concat('%',#{name},'%')
and addinfo.yearaddcode like concat('%',#{yearAddCode},'%')
and addinfo.requeststatus = #{requestStatus}
AND ( unit.baseunitid IN (SELECT _unit.baseunitid FROM budget_unit _unit WHERE _unit.managers is NOT NULL AND CONCAT(',',_unit.managers,',') LIKE CONCAT('%,', #{userId}, ',%') )
or (1=1 ${authSql})
)
ORDER BY addinfo.createtime desc
